    let modbusValues = [];
    let address = 100; // Starting Modbus address, adjust as needed

    // Process all elements in the row except 'Test Step' and 'Duration'
    Object.keys(row).forEach(tag => {
        if (tag !== "Test_step" && tag !== "Duration") {
            let buffer = Buffer.alloc(4);
            buffer.writeFloatBE(parseFloat(row[tag]), 0);
            modbusValues.push(buffer.readUInt16BE(2)); // High 16 bits
            modbusValues.push(buffer.readUInt16BE(0)); // Low 16 bits
        }
    });

