                payload: {
                    'unitId': 1,
                    'fc': 16,
                    'address': Math.min(currentSetpoint["MODBUS Start Address"], voltageSetpoint["MODBUS Start Address"]) - 400001,
                    'quantity': 4,
                    'value': [0, 0, 0, 0]  // Set both to zero
                }
            };

            var triggerMsg = { payload: "Test Completed" };

            // Send email alert if permStatus is false
